const { Client, LocalAuth } = require('whatsapp-web.js');
const qrcode = require('qrcode-terminal');
const fs = require('fs');
const bodyParser = require('body-parser');
const express = require('express')
const app = express()
const port = 3000
const { MessageMedia } = require('whatsapp-web.js');
// const media = new MessageMedia('image/png', "");
// app.use(express.json());
// console.log(media)

app.use(bodyParser.urlencoded({ extended: false }));
// // app.use(bodyParser.json());
// const parser = bodyParser.urlencoded({extended:false})


const client = new Client({
    authStrategy: new LocalAuth(),
    puppeteer: { headless: true },

});
client.initialize();
client.on('qr', (qr) => {
    qrcode.generate(qr);
});``
client.on('authenticated', () => {
    console.log('AUTHENTICATED');
});
client.on('auth_failure', msg => {
    // Fired if session restore was unsuccessful
    console.error('AUTHENTICATION FAILURE', msg);
});

client.on('ready', () => {
    console.log('Client is ready!');
});

app.post('/kirim',async(req, res) => {

    let pesan = req.body.pesan
    let nomer = req.body.nomer
    // res.send(peslet sessionData;a
    res.send(req.body)
    const media = await MessageMedia.fromUrl('http://pii.or.id/uploads/dummies.pdf',);

    
    client.sendMessage(nomer + "@c.us", media, {caption: pesan})
 

    // //console.log(req.body)
    console.log(nomer + "@c.us")
    console.log(pesan)
    // res.sendStatus(200);
})

app.listen(port, () => {
    console.log(`Example app listening on port ${port}`)
})
client.on('message', msg => {
    if (msg.body == '/ping') {
        msg.reply('pong');
    }
});

